function sendemail() {
    var t = { Name: document.getElementById("ContactUs_Name").value, FromEmailAddress: document.getElementById("ContactUs_From").value, Subject: document.getElementById("ContactUs_Subject").value, Purpose: document.getElementById("ContactUs_Purpose").value, Details: document.getElementById("ContactUs_Details").value, Phone: document.getElementById("ContactUs_Phone").value },
        i = $("#main-contact-form"),
        n = $('<div class="form_status"><\/div>');
    $.ajax({
        url: basePath + "/core/SendEmail",
        type: "POST",
        headers: { ApiKey: "XYZ", Authorization: localStorage.Authorization },
        data: JSON.stringify(t),
        contentType: "application/json; charset=utf-8",
        dataType: "json",
        async: !1,
        beforeSend: function() { i.prepend(n.html('<p><i class="fa fa-spinner fa-spin"><\/i> Email is sending...<\/p>').fadeIn()) },
        success: function(t, i, r) { r.status == 200 ? (n.html('<p class="text-success">Thank you for contacting us. As early as possible  we will contact you<\/p>').delay(3e3).fadeOut(), showMessage("ContactUs", "info", "Status", "Query/Feedback submitted successfully")) : (n.html('<p class="text-danger">error submmitting query/feedback<\/p>').delay(3e3).fadeOut(), showMessage("ContactUs", "error", "Status", "error submmitting query/feedback")) },
        error: function() {
            n.html('<p class="text-success">Thank you for contacting us. As early as possible  we will contact you<\/p>').delay(3e3).fadeOut();
            showMessage("ContactUs", "info", "Status", "Query/Feedback submitted successfully")
        }
    })
}
